import XCTest
import XCTVapor

@testable import FirebaseJWTMiddleware

final class FirebaseJWTMiddlewareTests: XCTestCase {
        let token = """
eyJhbGciOiJSUzI1NiIsImtpZCI6IjgzYTczOGUyMWI5MWNlMjRmNDM0ODBmZTZmZWU0MjU4Yzg0ZGI0YzUiLCJ0eXAiOiJKV1QifQ..I4NqedoNtJurciGNvvd2OnycXEdaem5Prwz0QtYlGgGTmRcsP0PEJjL6KHCayJyvH4DG_hGFRSmklKXI-_amVUy1Xv2-G0ooFKcxJAzTkfjDfJRwlYdaQGutConoUWcbBjQHPkGoHntvElFGm64PKH28iXr6uILDzfEJgHMoGkIGb-A89LCC4th4SZxNXg21Ms9YVsR06I66p7gBwx7CrWxlEB-xdHmVFJnQ9kXLf4OceuvmkRg_KXqWTaF9KXdCa9Nzr3DE6jNFuhT7ZhrU9rwDn6ZUGm8MTYAmScFiEnqC4PoQcrfgfxpkeB3a9IPM4CWGMxaBAM5fHwJ0HvtUfQ
"""
    static let applicationIdentifier: String = "phrase-book-vapor"

    func testFirebase() throws {
        // creates a new application for testing
        let app = Application(.testing)
        defer { app.shutdown() }
        
        app.firebaseJwt.applicationIdentifier = FirebaseJWTMiddlewareTests.applicationIdentifier
        
        app.get("test") { req async in
            let payload = try? await req.firebaseJwt.verify()
            return payload?.email ?? "none"
        }
        
        var headers = HTTPHeaders()
        headers.bearerAuthorization = .init(token: token)
        
        try app.test(.GET, "test", headers: headers) { res in
            print(res.body.string)
            XCTAssert(res.body.string == "brsatamer@gmail.com")
        }
    }
    
    static var allTests = [
        ("testFirebase", testFirebase),
    ]
}
